const sliderState={activeSlide:2};const stringToNumber=function(a){return +a};const moveSlider=function(a){document.querySelector(".slider").style.left=a+"px"};const showComment=function(a){document.querySelector(".comments .comment:nth-child("+sliderState.activeSlide+")").classList.toggle("active");document.querySelector(".comments .comment:nth-child("+a+")").classList.toggle("active")};const setActiveSlide=function(a){document.querySelector(".slider .slide:nth-child("+sliderState.activeSlide+")").classList.toggle("active");document.querySelector(".slider .slide:nth-child("+a+")").classList.toggle("active")};const setActiveIndicator=function(a){document.querySelector(".indicators .indicator:nth-child("+sliderState.activeSlide+")").classList.toggle("active");document.querySelector(".indicators .indicator:nth-child("+a+")").classList.toggle("active")};const changeSlide=function(c){const b=document.querySelector(".slider .slide").offsetWidth;const a=-b*(c-1);moveSlider(a);showComment(c);setActiveSlide(c);setActiveIndicator(c);sliderState.activeSlide=c};document.querySelector(".slider-container").addEventListener("click",function(a){switch(a.target.parentNode.className){case"slide":changeSlide(stringToNumber(a.target.parentNode.getAttribute("data-index")));break}});document.querySelector(".indicators").addEventListener("click",function(a){switch(a.target.className){case"indicator":changeSlide(stringToNumber(a.target.getAttribute("data-index")));break}});